package Patterns;

public class Half_pyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int k=1;
		for(int i=1;i<=5;i++)
		{
			for(int j=1;j<=i;j++)
			{
				int count=j;
				System.out.print(count);
				count++;
				
			}
			System.out.println();
		}
		

	}

}
